async function createData(){
    try {
      let res = await fetch(`http://localhost:9090/todos`, {
        method: 'POST',
        headers: { 
          'Content-type': 'application/json',
          'Authorization': `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..VtoT7t0WMMtfBAgiB8WowRi43yQOSQ0hsOhuB2OJ5WQ`
        },
        body: JSON.stringify({
          userId: 48,
          title: "New loyal Minskin",
          completed: false
        })
      });
      if (res.ok) {
        let data = await res.json();
        return data;
      } else {
        return `server responded with a ${res.status} error.`
      }
    } catch (error) {
      return 'error';
    }
  }
  
